import axios from "axios";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router";
import { Link } from "react-router-dom";
import Button from "react-bootstrap/Button";
import Card from "react-bootstrap/Card";
const imgURL = "https://image.tmdb.org/t/p/w342";

const SingleSerie = () => {
  const { id } = useParams();
  const [serie, setSerie] = useState({});
  useEffect(() => {
    axios
      .get(
        `https://api.themoviedb.org/3/tv/${id}?api_key=680e609baa2671cc67cf4d6797aaff0b&language=en-US`
      )
      .then((res) => setSerie(res.data));
  }, []);

  const addToFavorites = () => {
    axios
      .post("/api/users/favorites", {
        title: serie.name,
        image: imgURL + serie.poster_path,
      })
      .then((res) => {
        console.log(res);
        return res.data;
      })
      .then(() => alert("favorito agregado"));
  };
  return (
 /*    <div>
      <Link to="/">
        <h4>Volver a main</h4>
      </Link>
      <h1>{serie.name}</h1>
      <img src={imgURL+serie.poster_path} alt="" />
      <h2>{serie.overview}</h2>
      <h3>Number of seasons: {serie.number_of_seasons}</h3>
      <button onClick={addToFavorites}>Agregar a fav</button>
    </div> */
    <>
      <Link to="/">
        <h4>Volver a main</h4>
      </Link>
      <Card style={{ width: "18rem" }}>
        <Card.Img variant="top" src={imgURL+serie.poster_path} />
        <Card.Body>
          <Card.Title>{serie.name}</Card.Title>
          <Card.Text>{serie.overview}</Card.Text>
          <Button variant="primary" onClick={addToFavorites}>
            Agregar a favoritos
          </Button>
        </Card.Body>
      </Card>
    </>
  );
};

export default SingleSerie;
