import React from 'react'
import { Link } from 'react-router-dom'

const ShowList = ({items}) => {
  return (
    <div>
    <ul>
      {items.map((item) => (
        <li key={item.id}>
          <article >
            {item.title?<h2><Link to={`/movie/${item.id}`}>{item.title}</Link></h2>:<h2><Link to={`/serie/${item.id}`}>{item.title}</Link></h2>}
            <ol>
            </ol>
          </article>
        </li>
      ))}
    </ul>
  </div>
  )
}

export default ShowList
