import React from "react";
import "bootstrap/dist/css//bootstrap.min.css";
import { Button, Nav } from "react-bootstrap";
import { useSelector, useDispatch } from "react-redux";
import { deleteUser}  from "../store/states/user"
import { useNavigate } from "react-router";
import axios from "axios";

const Navbar = () => {
  const navigate = useNavigate();
  const user = useSelector((state) => state.user);
  const logoutHandler = () =>{
    axios
      .post("/api/users/logout")
      .then((res) => {
        dispatch(deleteUser(res.data))
        return res.data})
      .then(()=>navigate("/"))
  }
  return (
    <Nav className="navbar navbar-expand-lg navbar-light bg-light">
      <a className="navbar-brand" href="/">
        Bienvenidos a PELISBRODER
      </a>
      {user.email ? (
        <>
          <Button className="navbar-brand" href="/">
            favorites
          </Button>
          <Button onClick={logoutHandler} className="navbar-brand" href="/">
            logout
          </Button>
        </>
      ) : (
        <>
          <Button className="navbar-brand" href="/register">
            register
          </Button>
          <Button className="navbar-brand" href="/login">
            login
          </Button>
        </>
      )}
    </Nav>
  );
};

export default Navbar;
