import axios from "axios";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router";
import { Link } from "react-router-dom";
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
const imgURL = "https://image.tmdb.org/t/p/w342";


const SingleMovie = () => {
  const { id } = useParams();
  const [movie, setMovie] = useState({});
  useEffect(() => {
    axios
      .get(
        `https://api.themoviedb.org/3/movie/${id}?api_key=680e609baa2671cc67cf4d6797aaff0b&language=en-US`
      )
      .then((res) => setMovie(res.data));
  }, []);

  const addToFavorites = () => {
    console.log("PEPERRI");
    axios
      .post("/api/users/favorites", {
        title: movie.title,
        image: imgURL + movie.poster_path,
      })
      .then((res) => {
        console.log(res);
        return res.data;
      })
      .then(()=>alert("favorito agregado"))
  };

  return (
/*     <div>
      <Link to="/">
        <h4>Volver a main</h4>
      </Link>
      <h1>{movie.title}</h1>
      <img src={imgURL + movie.poster_path} alt={movie.id} />
      <h3>{movie.tagline}</h3>
      {movie.overview ? (
        <h3>{movie.overview}</h3>
      ) : (
        <h3>No hay overview para esta pelicula</h3>
      )}
      <button onClick={addToFavorites}>Agregar a fav</button>
    </div> */
    <Card style={{ width: '18rem' }}>
    <Card.Img variant="top" src="holder.js/100px180" />
    <Card.Body>
      <Card.Title>{movie.title}</Card.Title>
      <Card.Text>
        {movie.overview}
      </Card.Text>
      <Button variant="primary" onClick={addToFavorites}>Go somewhere</Button>
    </Card.Body>
  </Card>
  );
};

export default SingleMovie;
