import React, { useEffect, useState } from "react";
import axios from "axios";
const tmdb = "https://api.themoviedb.org/3/search/movie/";
const key = "api_key=680e609baa2671cc67cf4d6797aaff0b";

const Browser = () => {
  const [items, setItems] = useState([]);
  const [q, setQ] = useState("");
  const [searchParam] = useState(["capital", "name"]);

  /* searchHandler = (e) => {

  } */

  useEffect(() => {
    axios
      .get(`${tmdb}${key}&query=furious`)
      .then((res) => res.data)
      .then((data) => setItems(data.results));
  }, []);
  console.log(items);
  return (
    <>
      <div >
        <label >
          <input placeholder="Pone el nombre de tu peli..."/>
          <span >Search movies here</span>
        </label>
      </div>

      <div>
        <ul>
          {items.map((item) => (
            <li>
              <article key={item.id}>
                <h2>{item.title}</h2>
                <ol>
                  <ol>
                    overview: <span>{item.overview}</span>
                  </ol>
                </ol>
              </article>
            </li>
          ))}
        </ul>
      </div>
    </>
  );
};

export default Browser;
