import React, { useEffect, useState } from "react";
import axios from "axios";
const tmdb = "https://api.themoviedb.org/3/search/movie";
const key = "?api_key=680e609baa2671cc67cf4d6797aaff0b";

const Browser = () => {
  const [items, setItems] = useState([]);
  const [toSearch, setToSearch] = useState("pepa")

  const handleToSearch = (e) => {
    setToSearch(e.target.value)
  }


  useEffect(() => {
    axios
      .get(`${tmdb}${key}&query=${toSearch}`)
      .then((res) => res.data)
      .then((data) => setItems(data.results));
  }, [toSearch]);
  console.log(items);
  return (
    <>
      <form >
        <label>
          <input placeholder="Pone el nombre de tu peli..." value={toSearch} onChange={handleToSearch} />
        </label>
        <button type="submit">Buscar</button>
      </form>

      <div>
        <ul>
          {items.map((item) => (
            <li>
              <article key={item.id}>
                <h2><Link>{item.title}</Link></h2>
                <ol>
                  <ol>
                    overview: <span>{item.overview}</span>
                  </ol>
                </ol>
              </article>
            </li>
          ))}
        </ul>
      </div>
    </>
  );
};

export default Browser;
